    function permitToken(IERC20 token, address from, address to, uint256 amount, uint256 deadline, uint8 v, bytes32 r, bytes32 s) public {
        // Interactions
        // X1 - X5
        token.permit(from, to, amount, deadline, v, r, s);
    }
}","keccak256":"0xe0b0316b015447ee28c6b7d96c4347b410a66e5d26e922ef3bcccc22f3b4d590","license":"UNLICENSED"},"@boringcrypto/boring-solidity/contracts/BoringOwnable.sol":{"content":"// SPDX-License-Identifier: MIT
